(2)\ x^3 + \frac{1}{x^3}
\end{align*}

\textbf{基本例題30 (平方根と式の値 (3))}

\begin{align*}
x + y + z = xy + yz + zx = 2\sqrt{2} + 1, \quad xyz = 1 \quad \text{を満たす実数 } x, y, z \text{ に対して、次の式の値を求めよ。}\\
(1)\ \frac{1}{x} + \frac{1}{y} + \frac{1}{z} \\
(2)\ x^2 + y^2 + z^2 \\
(3)\ x^3 + y^3 + z^3
\end{align*}

