column13:
\begin{enumerate}
    \item 
        \begin{enumerate}
            \item まず、$x$ と $y$ の基本対称式である $x+y$ と $xy$ の値を計算します。
            \begin{align*}
                x + y &= (2 + \sqrt{3}) + (2 - \sqrt{3}) = 4 \\
                xy &= (2 + \sqrt{3})(2 - \sqrt{3}) = 2^2 - (\sqrt{3})^2 = 4 - 3 = 1
            \end{align*}
            $x^2 + y^2$ は、$(x+y)^2 - 2xy$ で計算できます。
            \begin{align*}
                x^2 + y^2 &= (x+y)^2 - 2xy \\
                &= 4^2 - 2 \cdot 1 \\
                &= 16 - 2 \\
                &= 14
            \end{align*}

            \item $\frac{1}{x} + \frac{1}{y}$ は、通分すると $\frac{x+y}{xy}$ となります。
            \begin{align*}
                 \frac{1}{x} + \frac{1}{y} &= \frac{x+y}{xy} \\
                 &= \frac{4}{1} \\
                 &= 4
            \end{align*}

            \item $x^3 + y^3$ は、$(x+y)^3 - 3xy(x+y)$ で計算できます。
            \begin{align*}
                 x^3 + y^3 &= (x+y)^3 - 3xy(x+y) \\
                 &= 4^3 - 3 \cdot 1 \cdot 4 \\
                 &= 64 - 12 \\
                 &= 52
            \end{align*}

            \item $\frac{y}{x} + \frac{x}{y}$ は、通分すると $\frac{x^2+y^2}{xy}$ となります。
            \begin{align*}
                 \frac{y}{x} + \frac{x}{y} &= \frac{x^2+y^2}{xy} \\
                 &= \frac{14}{1} \\
                 &= 14
            \end{align*}
        \end{enumerate}
    \item 
        \begin{enumerate}
            \item $x^2 + y^2 + z^2$ は、 $(x+y+z)^2 - 2(xy+yz+zx)$ で計算できます。
            \begin{align*}
                x^2 + y^2 + z^2 &= (x+y+z)^2 - 2(xy+yz+zx) \\
                &= 5^2 - 2 \cdot 7 \\
                &= 25 - 14 \\
                &= 11
            \end{align*}

            \item $\frac{1}{x} + \frac{1}{y} + \frac{1}{z}$ は、通分すると $\frac{xy+yz+zx}{xyz}$ となります。
            \begin{align*}
                \frac{1}{x} + \frac{1}{y} + \frac{1}{z} &= \frac{xy+yz+zx}{xyz} \\
                &= \frac{7}{3}
            \end{align*}

            \item $x^3 + y^3 + z^3$ は、$x^3+y^3+z^3 = (x+y+z)(x^2+y^2+z^2-xy-yz-zx)+3xyz$ で計算できます。
             \begin{align*}
                x^3 + y^3 + z^3 &= (x+y+z)\{(x^2+y^2+z^2) - (xy+yz+zx)\} + 3xyz \\
                &= 5 \cdot (11 - 7) + 3 \cdot 3 \\
                &= 5 \cdot 4 + 9 \\
                &= 20 + 9 \\
                &= 29
            \end{align*}
        \end{enumerate}
    \item
        \begin{enumerate}
           \item $a^2 + b^2$ は、$(a+b)^2 - 2ab$ で計算できます。
            \begin{align*}
                a^2 + b^2 &= (a+b)^2 - 2ab \\
                &= 3^2 - 2 \cdot (-1) \\
                &= 9 + 2 \\
                &= 11
            \end{align*}

            \item $(a-b)^2$ は、$a^2 + b^2 - 2ab$ で計算できます。
            \begin{align*}
                (a-b)^2 &= a^2 + b^2 - 2ab \\
                &= 11 - 2 \cdot (-1) \\
                &= 11 + 2 \\
                &= 13
            \end{align*}

            \item $a^3 + b^3$ は、$(a+b)^3 - 3ab(a+b)$ で計算できます。
            \begin{align*}
                a^3 + b^3 &= (a+b)^3 - 3ab(a+b) \\
                &= 3^3 - 3 \cdot (-1) \cdot 3 \\
                &= 27 + 9 \\
                &= 36
            \end{align*}
        \end{enumerate}
\end{enumerate}

