\textbf{基本例題 44(1) 集合の記号と表し方}

\begin{enumerate}
    \item $42$ の正の約数全体の集合を $A$ とする。次の □ の中に，$\in$ または $\notin$ のいずれか適するものを書き入れよ。
    \begin{align*}
        &(\mathrm{ア}) \, 7 \, □ \, A \\
        &(\mathrm{イ}) \, 9 \, □ \, A \\
        &(\mathrm{ウ}) \, -2 \, □ \, A
    \end{align*}
\end{enumerate}

column12:
\begin{enumerate}
    \item $60$ の正の約数全体の集合を $B$ とする。次の □ の中に，$\in$ または $\notin$ のいずれか適するものを書き入れよ。
    \begin{align*}
        &(\mathrm{ア}) \, 5 \, □ \, B \\
        &(\mathrm{イ}) \, 16 \, □ \, B \\
        &(\mathrm{ウ}) \, -3 \, □ \, B
    \end{align*}
    \item $C = \{x \,|\, xは20以下の3の倍数\}$ とする。次の □ の中に，$\in$ または $\notin$ のいずれか適するものを書き入れよ。
        \begin{align*}
            &(\mathrm{ア}) \, 10 \, □ \, C \\
            &(\mathrm{イ}) \, 12 \, □ \, C \\
            &(\mathrm{ウ}) \, 21 \, □ \, C
        \end{align*}
    \item $D = \{1, 2, 3, 4, 5, 6\}$ とする。次の □ の中に，$\in$ または $\notin$ のいずれか適するものを書き入れよ。
        \begin{align*}
            &(\mathrm{ア}) \, 4 \, □ \, D \\
            &(\mathrm{イ}) \, 7 \, □ \, D \\
            &(\mathrm{ウ}) \, 0 \, □ \, D
        \end{align*}
\end{enumerate}

column13:
\begin{enumerate}
    \item 
        $60$ の正の約数全体の集合 $B$ は、$B=\{1, 2, 3, 4, 5, 6, 10, 12, 15, 20, 30, 60\}$ となる。
        \begin{itemize}
            \item[(ア)] $5$ は集合 $B$ の要素なので、$5 \in B$
            \item[(イ)] $16$ は集合 $B$ の要素ではないので、$16 \notin B$
            \item[(ウ)] $-3$ は集合 $B$ の要素ではないので、$-3 \notin B$
        \end{itemize}
    \item
        $C = \{x \,|\, xは20以下の3の倍数\}$ より、$C = \{3, 6, 9, 12, 15, 18\}$ となる。
        \begin{itemize}
             \item[(ア)] $10$ は集合 $C$ の要素ではないので、$10 \notin C$
            \item[(イ)] $12$ は集合 $C$ の要素なので、$12 \in C$
            \item[(ウ)] $21$ は集合 $C$ の要素ではないので、$21 \notin C$
         \end{itemize}
    \item
        $D = \{1, 2, 3, 4, 5, 6\}$ より、
        \begin{itemize}
             \item[(ア)] $4$ は集合 $D$ の要素なので、$4 \in D$
            \item[(イ)] $7$ は集合 $D$ の要素ではないので、$7 \notin D$
            \item[(ウ)] $0$ は集合 $D$ の要素ではないので、$0 \notin D$
        \end{itemize}
\end{enumerate}

