            \item $3x^2 - 10x + 3 = 0$
            \item $2x^2 + 3x - 1 = 0$
            \item $12x^2 + 17x + 6 = 0$
            \item $4x^2 - 20x + 25 = 0$
             \item $2x(x-2)=3(x-1)-4$
        \end{enumerate}
