column9:
\textbf{基本例題 100: 2次方程式の実数解の個数 (1)}

\begin{enumerate}
    \item 次の 2 次方程式の実数解の個数を求めよ。ただし、(イ) の $k$ は定数とする。
    \begin{enumerate}
        \item $x^2 - 3x + 1 = 0$
        \item $x^2 + 6x - 2k + 1 = 0$
    \end{enumerate}
    \item $x$ の 2 次方程式 $x^2 + 2mx + 3m + 10 = 0$ が重解をもつとき、定数 $m$ の値を求めよ。また、そのときの方程式の解を求めよ。
\end{enumerate}

column12:
\begin{enumerate}
    \item 次の2次方程式の実数解の個数を求めよ。
    \begin{enumerate}
        \item $2x^2 + 4x - 3 = 0$
        \item $x^2 - 2kx + k + 2 = 0$ ( $k$ は定数)
    \end{enumerate}
    \item 2次方程式 $x^2 + (m-1)x + m + 2 = 0$ が重解をもつとき、定数 $m$ の値を求めよ。また、そのときの重解を求めよ。
\end{enumerate}

column13:
\begin{enumerate}
    \item (ア) $2x^2 + 4x - 3 = 0$ について、判別式 $D$ を計算します。
        \begin{align*}
            D &= 4^2 - 4 \cdot 2 \cdot (-3) \\
              &= 16 + 24 \\
              &= 40
        \end{align*}
        $D = 40 > 0$ なので、異なる2つの実数解を持ちます。

    \item (イ) $x^2 - 2kx + k + 2 = 0$ について、判別式 $D$ を計算します。
        \begin{align*}
            D &= (-2k)^2 - 4 \cdot 1 \cdot (k + 2) \\
              &= 4k^2 - 4k - 8 \\
              &= 4(k^2 - k - 2)
        \end{align*}

        $D=0$ となる $k$ の値を求めるために、
        \begin{align*}
            k^2 - k - 2 &= 0 \\
            (k-2)(k+1) &= 0
        \end{align*}

        したがって、$k=2$ または $k=-1$ です。

        実数解の個数は $D$ の符号によって変化します。

        \begin{itemize}
            \item $D > 0$ 、つまり $k < -1$ または $k > 2$ のとき、異なる2つの実数解を持ちます。
            \item $D = 0$ 、つまり $k = -1$ または $k = 2$ のとき、重解を持ちます。
            \item $D < 0$ 、つまり $-1 < k < 2$ のとき、実数解を持ちません。
        \end{itemize}

    \item $x^2 + (m-1)x + m + 2 = 0$ が重解をもつとき、判別式 $D = 0$ となります。
        \begin{align*}
            D &= (m-1)^2 - 4 \cdot 1 \cdot (m+2) \\
              &= m^2 - 2m + 1 - 4m - 8 \\
              &= m^2 - 6m - 7
        \end{align*}
        $D = 0$ より、
        \begin{align*}
            m^2 - 6m - 7 &= 0 \\
            (m-7)(m+1) &= 0
        \end{align*}
        したがって、$m = 7$ または $m = -1$ です。

        \begin{itemize}
            \item $m = 7$ のとき、方程式は $x^2 + 6x + 9 = 0$ となり、$(x+3)^2 = 0$ より、重解は $x = -3$ です。
            \item $m = -1$ のとき、方程式は $x^2 - 2x + 1 = 0$ となり、$(x-1)^2 = 0$ より、重解は $x = 1$ です。
        \end{itemize}

        よって、$m=7$ のとき重解 $x=-3$ を持ち、$m=-1$ のとき重解 $x=1$ を持ちます。
\end{enumerate}

