    \[
    \begin{array}{cccccccc}
        x & 1 & 3 & 8 & 5 & 4 & 6 & 2 & 9 \\
        y & 2 & 2 & 6 & 7 & 3 & 5 & 3 & 8
    \end{array}
    \]
    \item
    \[
    \begin{array}{cccccccc}
        x & 38 & 46 & 20 & 48 & 18 & 27 & 11 & 33 \\
        y & 12 & 15 & 25 & 11 & 30 & 21 & 38 & 30
    \end{array}
    \]
    \item
    \[
    \begin{array}{cccccccc}
        x & 1.3 & 3.3 & 4.9 & 2.2 & 5.7 & 3.6 & 2.7 & 4.0 \\
        y & 2.6 & 4.2 & 2.0 & 1.3 & 4.2 & 1.2 & 4.1 & 3.6
    \end{array}
    \]
\end{enumerate}

